import React from 'react';
import BedtimeSharpIcon from '@mui/icons-material/BedtimeSharp';
import LightModeSharpIcon from '@mui/icons-material/LightModeSharp';
import Switch from '@mui/material/Switch';

import { ThemeContext } from '../wrappers/NightMode';

export default function NightModeToggle() {
    const themeContext = React.useContext(ThemeContext);

    return <div aria-label='toggle night mode' style={{ display: 'flex', alignItems: 'center' }}>
        {
            themeContext.theme.palette.mode === 'dark' ? <BedtimeSharpIcon aria-label='night mode' /> : <LightModeSharpIcon aria-label='light mode' />
        }
        <Switch
            checked={themeContext.theme.palette.mode === 'dark'}
            onClick={themeContext.colorMode.toggleDarkMode}
        />

    </div>
}
